def __str__(self):
    """Return string representation in format of: [Quest Submission __str__] - ()
    e.g. "Difficult Quest (1) - (1)" for the first question in the student's first quest submission
    """
    if self.question:
        return f"{self.submission} - ({self.question.ordinal})"
    else:
        return f"{self.submission} - (?)"

@classmethod
    def next_ordinal(cls, quest):
        """Return the next ordinal value for a question in this quest"""
        last_question = Question.objects.filter(quest=quest).last()
        if last_question:
            return last_question.ordinal + 1
        else:  # no questions in this quest yet
            return 1

