export type Result<T, E extends string> = SuccessResult<T> | FailureResult<E>;

export type SuccessResult<T> = { ok: true; value: T };
export type FailureResult<E extends string> = {
  ok: false;
  error: Error;
  code?: E;
};

export function success<T>(value: T): SuccessResult<T> {
  return { ok: true, value };
}
export function failure<E extends string>({
  error,
  code,
}: {
  error: Error;
  code?: E;
}): FailureResult<E> {
  return { ok: false, error, code };
}

