@classmethod
def from_orm(cls, problem: "Problem") -> "ProblemListResponse":
    if not problem.submissions:
        return cls(num=problem.num, status=0)

    for submission in problem.submissions:
        if submission.status == CodeSubmissionStatus.SOLVED:
            return cls(num=problem.num, status=CodeSubmissionStatus.SOLVED.value)

    if problem.submissions[0].status == CodeSubmissionStatus.RUNNING:
        status = CodeSubmissionStatus.RUNNING.value
    else:
        status = CodeSubmissionStatus.WRONG.value
    return cls(num=problem.num, status=status)

